package java_package;

public class Apsara {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	System.out.println("These are the different types of variable :-");
	int a; // create bucket a which can store integer value
	a= 10; // 10 is assigned to a
	double b; // create bucket b which can store decimal value
	b= 10.5; // 10.5 is assigned to b
	String c; // create bucket c which can store word value
	c=" Apsara"; // Apsara is assigned to c
	char d; // create bucket d which can store letter value
	d='g'; // G is assigned to d
	
	System.out.println("This is my integer value " + a );
	System.out.println("This is my decimal value " + b);
	System.out.println("This is my string value " + c);
	System.out.println("This is my character value = " + d);
	

	
		
		
		
		
		
		
	}

}
