package java_package;

public class Java_programme_10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// How to increment and decrement in Java
		
		int a; // create a bucket a
		a = 10; // assign value 10 to variable a
		
		a++; // '++' instructs an increment to the value
		System.out.println("Increment value is " + a);
		
		a=10;
		a--; // '--' instructs a decrement to the value
		System.out.println("Increment value is " + a);
		
		// NOTE: Computer reads the codes line by line, therefore if you didnt write a=10 in line 16, it would get the last value from line 14
		
		
	}

}
