package java_package;

public class Java_programme_11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// How to Compare Number if/else
		
		int a = 1; // value 1 is assigned to variable a
		int b = 1000; // value 1000 is assigned to variable b
		
		System.out.println("Is a greater than b? ");
		
		// Asking computer to print only one correct answer from below
		if(a>b)          // Option 1 (IF a>b is correct, print below)
		{
		System.out.println("Yes, a is greater than b ");
		}
		
		else             // Option 2 (or ELSE print below)
		{
			System.out.println("No, a is not greater than b ");
		}
		
		
	}

}
