package java_package;

public class Java_programme_13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// How to write your name 10 times using While Loop
		
		int count = 1; // starting point
		while(count<=10) // ending point
			{
			System.out.println("My name is Apsara Gurung"); // print value
			count++; // increment by 1
		}
	
		
		
	}

}
