package java_package;

public class Java_programme_7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//How to multiply two numbers in java
		
		int a; // create bucket a 
		int b;  // create bucket b
		a = 10;  // assign value 10 to variable a
		b = 5; // assign value 5 to variable b
		System.out.println("The multiple of a and b is " + (a*b));
		
		
		// Another way of doing it is by creating a formula below: -
		
		int multi = a*b; // multiplication formula is assigned to variable multi
		System.out.println("The multiple of a and b " + multi);
		
	}

}
