package java_package;

public class Java_programme_8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// How to divide two values in java
		
		int a; // create bucket a
		int b; // create bucket b
		a = 10; // assign value 10 to variable a
		b = 5; // assign value 5 to variable b
	
		System.out.println("Divided value is " + (a/b));
		
		
		// A different way of doing this will be to create variable with division formula as below: -
		
		int divide = a/b;
		System.out.print("Divided value is " + divide);
		
		
	}

}
