package java_package;

public class Java_programme_9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// How to write jave code to print the remainder number from a division 
		
		int a; // create bucket a
		int b; // create bucket b
		a = 10; // assign value 10 to variable a
		b = 3; // assign value 3 to variable b
		System.out.println("The remainder of 10/3 is " + (a%b));
		
		// A different way of doing this can be creating a variable and assigning a formula to it
		
		int remainder = a%b; // create variable remainder and assign the formula to it.
		System.out.println("The remainder of 10/3 is " + remainder);
	}

}
