package java_package;

public class variables_types {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a; // create a bucket a which can only store integer value
		double b; // create bucket b which can only store decimal value
		String c; // create bucket c which can only store one word value
		char d; // create bucket c which can only store one letter value
		
		a=100; // 100 is the value of a
		b=155.55; // 155.55 is the value of b
		c="sunday"; // sunday is the value of c
		d='y'; // y is the value of d
		System.out.println("The value of a " + a); // this programme will print the value of  a 100
		System.out.println("The value of b " + b); // this programme will print the value of b 155.55
		System.out.println("The value of c " + c);// this programme will print the value of c sunday
		System.out.println("The value of d " + d);// this programme will print the value of d y
		
		
		

	}

}
